import React from "react";

const dbms = require("../assets/certificates/dbma.png");
const java = require("../assets/certificates/java.png");
const c = require("../assets/certificates/c.png");
const excelr = require("../assets/certificates/Excelr.png");
const certificates = [
    
  {
    title: "Data Base Management - NPTEL",
    image: require("dbms"),
    link: "dbms",
  },
  {
    title: "Programming in Java - NPTEL",
    image: require("java"),
    link: "java",
  },
  {
    title: "Problem Solving Through Programming in C - NPTEL",
    image: require("c"),
    link: "c",
  },
  {
    title: "Data Analytics - ExcelR",
    image: require("excelr"),
    link: "excelr",
  },
  
  // Add more certificates similarly
];

const Certificates = () => {
  return (
    <section className="section max-width mt-[-100px] mb-[45px]" id="certificates">
      <h2 className="text-2xl md:text-3xl lg:text-4xl font-bold mb-15 text-left">Certifications</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {certificates.map((cert, index) => (
          <div
            key={index}
            className="shadow-lg p-4 rounded-xl border hover:shadow-xl transition duration-300"
          >
            <img
              src={cert.image}
              alt={cert.title}
              className="w-full h-48 object-cover rounded"
            />
            <h3 className="mt-4 text-xl font-semibold">{cert.title}</h3>
            <a
              href={cert.link}
              target="_blank"
              rel="noreferrer"
              className="text-blue-500 underline mt-2 inline-block"
            >
              View Certificate
            </a>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Certificates;
